mapper.select:
SELECT p_no, p_brand, p_cate, p_name, p_img, p_grade FROM products
WHERE p_brand = #{p_brand}
ORDER BY p_no ASC

SELECT p_no, p_brand, p_cate, p_name, p_img, p_grade FROM products
WHERE p_brand = #{p_brand}
AND p_cate = #{p_cate}
ORDER BY p_no ASC

SELECT p_no, p_brand, p_cate, p_name, p_img, p_grade FROM products
WHERE p_cate = #{p_cate}
ORDER BY p_no ASC

SELECT p_no, p_brand, p_cate, p_name, p_img, p_grade FROM products
WHERE p_name = #{p_name}
ORDER BY p_no ASC
